sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var camDataSyncUtils = new cam_DataSyncUtils();

	//gs.info("[deleteEntityClassification] Called. From " + current.effective_from + ", To " + current.effective_to);
	var entityGR = camDataSyncUtils.getEntityBySysId(current.getValue("entity"));
	if (entityGR) {
		var classification = entityGR.getValue("classification");
		//gs.info("[deleteEntityClassification] current classification " + classification);

		if (classification) {
			classification = cam.removeValueFromTokenizedString(classification, ",", current.class_type.toString());
			entityGR.setValue("classification", classification);
			entityGR.last_committed_update = entityGR.sys_mod_count;
			entityGR.setWorkflow(false);
			entityGR.autoSysFields(false);
			entityGR.update();
			//gs.info("[deleteEntityClassification] Entity updated with new classification " + classification);
		} else {
			//gs.info("[deleteEntityClassification] classification was null!");
		}
	} else {
		//gs.info("[deleteEntityClassification] Could not find entity record for " + current);
	}

})(current, previous);
