sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var gr = new GlideRecord('x_norca_med_cam_reserves');
	gr.addQuery('claim_number', current.sys_id);
	gr.query();

	if(gr.getRowCount()>0){
		while (gr.next()) {

			current.reserves='<table border="1"; style=""><tbody>';

			current.reserves=current.reserves+'<tr><td style="padding-bottom: 5px;padding-top: 5px;padding-right: 10px;padding-left:10px;color: white; text-align: left;" bgcolor="#67696B"><strong>Expense Payments:</strong></td><td style="padding-right: 10px;padding-left:10px;color: black; text-align: right;">' + gr.total_expense_payments.getReferenceDisplayValue() + '</td></tr>';

			current.reserves=current.reserves+'<tr>	<td style="padding-bottom: 10px;padding-top: 5px;padding-right: 10px;padding-left:10px;color: white; text-align: left;" bgcolor="#67696B"><strong>Outstanding Reserve:</strong></td><td style="padding-right: 10px;padding-left:10px;color: black; text-align: right;">' + gr.remaining_expense.getReferenceDisplayValue() + '</td></tr>';

			current.reserves=current.reserves+'</tbody></table>';

			current.reserves2='<table border="1"; style=""><tbody>';

			current.reserves2=current.reserves2+'<tr><td style="padding-bottom: 5px;padding-top: 5px;adding-right: 10px; padding-left:10px;color: white; text-align: left;" bgcolor="#67696B"><strong>Indemnity Payments:</strong></td><td style="padding-right: 10px;padding-left:10px;color: black; text-align: right;">' + gr.total_indemnity_payments.getReferenceDisplayValue() + '</td></tr>';

			current.reserves2=current.reserves2+'<tr><td style="padding-bottom: 10px;padding-top: 5px;padding-right: 10px;padding-left:10px;color: white; text-align: left;" bgcolor="#67696B"><strong>Outstanding Reserve:</strong></td><td style="padding-right: 10px;padding-left:10px;color: black; text-align: right;">' + gr.remaining_indemnity.getReferenceDisplayValue() + '</td></tr>';

			current.reserves2=current.reserves2+'</tbody></table>';
		}
	}
	else{
		current.reserves='There are no reserve details for this claim yet.';
	}

})(current, previous);
