sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	gs.info("[updateClaimParticipantBR] Role is " + current.role.toString());

	if (current.role) {
		var camDataSyncUtils = new cam_DataSyncUtils();
		switch(current.getValue("role")) {

			// Defense Counsel
			case "DINDATTY":
				if (camDataSyncUtils.isRecordEffectiveSimple(current, "effective_from", "effective_to")) {
					updateClaim(current.claim, "counsel", current.entity);
				}
				break;

			// Claim Specialist
			case "EXAMINER": 
				if (camDataSyncUtils.isRecordEffectiveSimple(current, "effective_from", "effective_to") && current.sys_updated_by == "oasissn") {
					var gr = new GlideRecord(cam.SCOPE_ID + "_entity");
					if (gr.get(current.entity) && gr.user) {
						updateClaim(current.claim, "assigned_to", gr.user);
					}
				}
				break;

			// Claimant
			case "CLAIMANT":
				if (camDataSyncUtils.isRecordEffectiveSimple(current, "effective_from", "effective_to")) {
					updateClaim(current.claim, "claimant", current.entity);
				}
				break;

			// Insured
			case "CLMINSURED":
				if (camDataSyncUtils.isRecordEffectiveSimple(current, "effective_from", "effective_to")) {
					updateClaim(current.claim, "u_insured", current.entity);
				}
				break;

		}
	}
})(current, previous);

function updateClaim(claimId, fieldName, value) {
	var gr = new GlideRecord( cam.SCOPE_ID + "_claim");
	if (gr.get(claimId) && gr.getValue(fieldName)!=value) {
		gr.setValue(fieldName, value);
		gr.setWorkflow(false);
		gr.autoSysFields(false);
		gr.update("updateClaimParticipantBR");
	}
}
