// 	});

	ScriptLoader.getScripts('x_norca_med_cam.cam_ClaimClientValidatio.jsdbx', function(){
		//console.log ("report_date " + g_form.getValue("report_date"));
		if (!g_form.getValue("report_date")) {
			var dt = new Date();
			g_form.setValue("report_date", x_norca_med_cam.cam_ClaimClientValidation.jsDateStringToUSLocaleString( dt.toLocaleDateString()));
		}

		//console.log("validationUtil loaded, calling validateClaimsDates");
		x_norca_med_cam.cam_ClaimClientValidation.validateClaimDates(g_form);
		//console.log("called validateClaimsDates");
	});

}
