sysauto_script.script:
var claimsGR = new GlideRecord("x_norca_med_cam_claim");
// claimsGR.setLimit(10000);
claimsGR.addNullQuery("assigned_to");
// policyGR.addQuery( "policy_cycle_type", "POLICY");
// policyGR.addQuery( "number", "031396");
claimsGR.query();
gs.info("[setAssignedTo] Processing " + claimsGR.getRowCount() + " records.");

var i = 0;
while (claimsGR.next()) {
	var participantGR = new GlideRecord("x_norca_med_cam_participant");
	participantGR.addQuery("claim", claimsGR.getUniqueValue());
	participantGR.addQuery("role", "EXAMINER");
	participantGR.addNotNullQuery("entity");
	participantGR.orderByDesc("effective_to_date");
	participantGR.setLimit(1);
	participantGR.query();
	gs.debug("[setAssignedTo] Claim " + participantGR.getDisplayValue() + " found " + participantGR.getRowCount() + " Specialist");
	if (participantGR.next()) {

		var entityGR = new GlideRecord("x_norca_med_cam_entity");
		if (entityGR.get(participantGR.getValue("entity")) && entityGR.getValue("user")) {
			i++;
			claimsGR.setValue( "assigned_to", entityGR.getValue("user"));
			claimsGR.setWorkflow(false);
			claimsGR.autoSysFields(false);
			gs.info("[setAssignedTo] Updated Assigned To for Claim " + claimsGR.getDisplayValue() + " to " + claimsGR.getDisplayValue("assigned_to"));
			claimsGR.update();
		}
	}
}
gs.info("[setAssignedTo] Updated " + i + " records.");
